mapper.select:
select
    /* 배변 횟수 평균 */
    ifnull(avg(poop_cnt), 0) as avgPoopCnt,

    /* 사료 상태를 점수로 변환하여 평균 산출 */
    ifnull(avg(case
    when food_status = '평소보다 많음' then 3
    when food_status = '평소와 같음' then 2
    else 1 end), 0) as avgFoodScore
from
    healthlog_tb
where
    pet_id = #{petId}
    and write_date between #{startDate} and #{endDate}
